import listSchema from './list_schema.js';
import taskSchema from './task_schema.js';
import userSchema from './user_schema';

const linkSchema = gql`
  type Query {
    _: Boolean
  }

  type Mutation {
    _: Boolean
  }

  type Subscription {
    _: Boolean
  }
`;

export default [linkSchema, listSchema, taskSchema, userSchema];

